#include <iostream>
#include <stdlib.h>
#include <string>

int main ()
{
	std::string str = "";
	int num [1000] = {-1};

	int c = 13;

	for (unsigned int i = 0; i < str.length(); ++i)
	{
		num[i] = str.at(i) - '0';
		std::cout << num[i];
	}

	unsigned long long thisNum = 1;

	for (int i = 0; i < c; ++i)
	{
		thisNum *= num[i];
	}

	printf("\nInitial number: %llu\n", thisNum);

	unsigned long long maxNum = thisNum;

	for (int i = 0; i + c < (int)str.length(); ++i)
	{
		if (thisNum != 0)
		{
			thisNum /= num[i];
			thisNum *= num[i + c];

			if (thisNum > maxNum)
			{
				maxNum = thisNum;
			}
		}
		else
		{
			for (int j = c - 1; j >= 0; ++j)
			{
				if (num[i + j] == 0)
				{
					i += j;
					break;
				}
			}

			if (i + c < (int)str.length())
			{
				thisNum = 1;
				for (int j = 0; j < c; ++j)
				{
					thisNum *= num[i + 1 + j];
				}
				if (thisNum > maxNum)
				{
					maxNum = thisNum;
				}
			}
			else
			{
				i = str.length();
			}
		}
	}

	printf("Best: %llu\n", maxNum);
}